import dayjs from "dayjs";
import * as pdfMake from "pdfmake/build/pdfmake";
import * as pdfFonts from 'pdfmake/build/vfs_fonts';

function DoadoresPDF(doadores, date) {
    pdfMake.vfs = pdfFonts.pdfMake.vfs;

    const win = window.open('', '_blank');

    const reportTitle = [
        {
            text: `Vida por Vidas Coromandel - ${dayjs(date).format("DD/MM/YYYY")}`,
            alignment: 'left',
            fontSize: 9,
            bold: true,
            margin: [15, 20, 0, 45] //left, top, right, botton;
        }
    ];

    const data = doadores.map((doador, i) => {

        if (doador.complemento === null) doador.complemento = '';

        return [
            { text: i + 1, fontSize: 10, margin: [0, 2, 0, 2] },
            { text: doador.nome, fontSize: 10, margin: [0, 2, 0, 2] },
            {
                text: `CPF: ${doador.cpf}\nRG: ${doador.rg}\nCel: ${doador.celular}`,
                fontSize: 10
                , margin: [0, 2, 0, 2]
            },
            {
                text: `Rua: ${doador.logradouro}\nBairro: ${doador.bairro}, ${doador.localidade}/${doador.estado}\n${doador.complemento}`
                , fontSize: 10
                , margin: [0, 2, 0, 2]
            }
        ]
    })

    const details = [
        {
            columns: [
                {
                    image: 'data:image/jpeg;base64,',
                    width: 100,
                    height: 51
                },
                {
                    text: [
                        { text: 'LISTA DE DOADORES\n', fontSize: 14, bold: true, alignment: 'center', margin: [0, 2, 0, 2] },
                        { text: 'COROMANDEL - HEMOCENTRO DE UBERLÂNDIA\n', fontSize: 12, bold: true, alignment: 'center', margin: [0, 2, 0, 2] },
                        { text: 'Responsável: Diego Santos, (34) 9 9000-0000 - Data: ' + dayjs(date).format("DD/MM/YYYY"), fontSize: 11, alignment: 'center', margin: [0, 2, 0, 25] },
                    ]
                },
                {
                    image: 'data:image/jpeg;base64,',
                    width: 65,
                    height: 74,
                    alignment: 'right',
                },
            ],
        },
        {
            layout: 'lightHorizontalLines',
            table: {
                headerRows: 1,
                widths: [20, '*', '*', 'auto'],
                body: [
                    [
                        { text: '', style: 'tableHeader', fontSize: 10 },//coluna
                        { text: 'NOME', style: 'tableHeader', fontSize: 10 },
                        { text: 'DOCUMENTAÇÃO', style: 'tableHeader', fontSize: 10 },
                        { text: 'ENDEREÇO', style: 'tableHeader', fontSize: 10 },
                    ],
                    ...data
                ],
            }
        }
    ];
    function footer(currentPage, pageCount) {
        return [
            {
                text: currentPage + '/' + pageCount,
                alignment: 'right',
                fontSize: 9,
                margin: [0, 10, 20, 0],
            }
        ]
    }

    const docDefinitions = {
        info: {
            title: 'Listage de doadores',
            author: 'Sistema controle de doadores'
        },
        pageSize: 'A4',
        pageMargins: [15, 50, 15, 40],
        header: [reportTitle],
        content: [details],
        footer: footer,
    }

    pdfMake.createPdf(docDefinitions).open({}, win); // Para exibir antes de realizar o download;
    //pdfMake.createPdf(docDefinitions).download(); // Para realizar o download direto;
}

export default DoadoresPDF;